      .score-number { font-size: 2.5em; font-weight: bold; margin: 10px 0; }
      .score-label { font-size: 1.1em; opacity: 0.9; }
      .btn-primary { background: #007bff; color: white; border: none; padding: 15px 30px; border-radius: 25px; font-size: 16px; font-weight: 600; cursor: pointer; transition: all 0.3s ease; }
      .btn-primary:hover { background: #0056b3; transform: translateY(-2px); box-shadow: 0 4px 12px rgba(0,123,255,0.3); }
      .btn-secondary { background: #6c757d; color: white; border: none; padding: 10px 20px; border-radius: 20px; cursor: pointer; }
      .interpretation-panel { background: white; color: #333; border-radius: 12px; padding: 25px; margin: 20px 0; }
      .alert { padding: 15px 20px; border-radius: 8px; margin: 15px 0; font-weight: 500; }
      .alert-success { background: #d4edda; border: 1px solid #c3e6cb; color: #155724; }
      .alert-info { background: #d1ecf1; border: 1px solid #bee5eb; color: #0c5460; }
      .alert-warning { background: #fff3cd; border: 1px solid #ffeaa7; color: #856404; }
      .alert-danger { background: #f8d7da; border: 1px solid #f5c6cb; color: #721c24; }
      .norm-comparison { display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: 15px; margin: 20px 0; }
      .norm-item { padding: 20px; border-radius: 10px; text-align: center; color: white; font-weight: bold; position: relative; }
      .norm-good { background: linear-gradient(135deg, #28a745, #20c997); }
      .norm-average { background: linear-gradient(135deg, #17a2b8, #20c997); }
      .norm-elevated { background: linear-gradient(135deg, #ffc107, #fd7e14); color: #333; }
      .norm-high { background: linear-gradient(135deg, #dc3545, #e83e8c); }
      .your-score { background: #007bff; color: white; padding: 15px; border-radius: 10px; text-align: center; margin: 20px 0; font-size: 1.2em; font-weight: bold; }
