# Server
server <- function(input, output, session) {
  values <- reactiveValues(calculated = FALSE)
  
  observeEvent(input$calculate, {
    # Sammle alle Antworten
    all_items <- sapply(1:12, function(i) input[[paste0("item_", i)]])
    
    # Prüfe ob alle beantwortet
    if (any(sapply(all_items, is.null))) {
      showNotification("🧍‍♂️ Bitte beantworten Sie alle 12 Fragen!", type = "warning")
      return()
    }
    
    # Berechne Scores
    scores <- as.numeric(all_items)
    values$self_score <- sum(scores[1:6])
    values$interpersonal_score <- sum(scores[7:12])
    values$total_score <- values$self_score + values$interpersonal_score
    values$percentile <- calculate_percentile(values$total_score)
    
    # T-Wert aus Tabelle
    t_vals <- c(45, 46, 48, 50, 51, 53, 54, 55, 56, 57, 58, 59, 61, 62, 63, 64, 65, 66, 67, 68, 69, 70, 72, 72, 74, 75, 76, 78, 78, 79, 80, 82, 82, 82, 82, 82, 82)
    names(t_vals) <- 12:48
    values$t_value <- t_vals[as.character(values$total_score)]
    if (is.na(values$t_value)) values$t_value <- 50
    
    values$calculated <- TRUE
    showNotification("🎯 Ergebnisse erfolgreich berechnet!", type = "message")
  })
  
  observeEvent(input$reset, {
    # Alle Inputs zurücksetzen
    lapply(1:12, function(i) {
      updateRadioButtons(session, paste0("item_", i), selected = character(0))
    })
    values$calculated <- FALSE
    showNotification("👥 Fragebogen wurde zurückgesetzt", type = "message")
  })
  
  # Outputs
  output$show_results <- reactive({ values$calculated })
  outputOptions(output, "show_results", suspendWhenHidden = FALSE)
  
  output$total_score <- renderText({
    if (values$calculated) as.character(values$total_score) else ""
  })
  
  output$self_score <- renderText({
    if (values$calculated) as.character(values$self_score) else ""
  })
  
  output$interpersonal_score <- renderText({
    if (values$calculated) as.character(values$interpersonal_score) else ""
  })
  
  output$percentile <- renderText({
    if (values$calculated) as.character(values$percentile) else ""
  })
  
  output$t_value <- renderText({
    if (values$calculated) as.character(values$t_value) else ""
  })
  
  output$interpretation <- renderUI({
    if (!values$calculated) return(NULL)
    
    score <- values$total_score
    percentile <- values$percentile
    t_val <- values$t_value
    
    # Interpretation basierend auf Perzentilen - MIT METHODISCHEN HINWEISEN
    if (percentile <= 31) {
      alert_class <- "alert-success"
      level <- "🧍‍♂️ Gutes Funktionsniveau"
      if (score == 12) {
        desc <- paste0("Sie haben das bestmögliche Ergebnis erreicht (", score, " Punkte, T-Wert: ", t_val, "). Dies ist der niedrigstmögliche Wert und deutet auf ein ausgezeichnetes Funktionsniveau der Persönlichkeit hin.")
      } else {
        desc <- paste0("Ihr Wert von ", score, " Punkten (T-Wert: ", t_val, ") liegt im ", percentile, ". Perzentil und deutet auf ein gutes Funktionsniveau der Persönlichkeit hin.")
      }
    } else if (percentile <= 69) {
      alert_class <- "alert-info"
      level <- "👤 Durchschnittliches Funktionsniveau"
      desc <- paste0("Ihr Wert von ", score, " Punkten (T-Wert: ", t_val, ") liegt im ", percentile, ". Perzentil und entspricht dem durchschnittlichen Bereich der Allgemeinbevölkerung.")
    } else if (percentile <= 93) {
      alert_class <- "alert-warning"
      level <- "🚶‍♂️ Leicht erhöhte Werte"
      desc <- paste0("Ihr Wert von ", score, " Punkten (T-Wert: ", t_val, ") liegt im ", percentile, ". Perzentil und könnte auf leichte Schwierigkeiten im Funktionsniveau hinweisen.")
    } else {
      alert_class <- "alert-danger"
      level <- "🧍‍♀️ Deutlich erhöhte Werte"
      desc <- paste0("Ihr Wert von ", score, " Punkten (T-Wert: ", t_val, ") liegt im ", percentile, ". Perzentil und deutet auf bedeutsame Schwierigkeiten im Funktionsniveau hin.")
    }
    
    tagList(
      div(class = paste("alert", alert_class),
          h4(level, style = "margin-bottom: 15px;"),
          p(desc, style = "margin-bottom: 0; font-size: 1.05em;")
      ),
      
      hr(),
      
      fluidRow(
        column(6,
               h5("📋 Referenzwerte:"),
               tags$ul(
                 tags$li("T₅₀ = 15 Punkte (Durchschnitt)"),
                 tags$li("T₇₀ = 33 Punkte (klinisch auffällig)")
               )
        ),
        column(6,
               h5("🧍‍♂️ Ihre Werte:"),
               tags$ul(
                 tags$li(paste0("Selbst-Bereich: ", values$self_score, "/24")),
                 tags$li(paste0("Interpersonal: ", values$interpersonal_score, "/24")),
                 tags$li(paste0("Gesamt: ", score, "/48"))
               )
        )
      ),
      
      hr(),
      
      div(style = "background: #f8f9fa; padding: 15px; border-radius: 8px; margin: 20px 0;",
          p(tags$strong("📊 Methodischer Hinweis: "), 
            "Die LPFS-BF hat einen Mindestwert von 12 Punkten (bestmögliches Ergebnis). Niemand kann niedrigere Werte erreichen. Das bedeutet, dass bei niedrigen Perzentilen nicht alle Prozentangaben unterhalb Ihres Werts liegen, sondern das die Normierung auf der gesamten theoretischen Verteilung basiert. Diese Studie wurde an einer nicht-klinischen Stichprobe durchgeführt.",
            style = "margin: 0; color: #6c757d; font-size: 0.9em;")
      ),
      
      div(style = "background: #f8f9fa; padding: 15px; border-radius: 8px; margin: 20px 0;",
          p(tags$strong("🧍‍♀️ Wichtiger Hinweis: "), 
            "Diese Auswertung dient ausschließlich zu Informations- und Bildungszwecken. Sie ersetzt keine professionelle psychologische Diagnostik oder Beratung. Bei anhaltenden Problemen wenden Sie sich an qualifizierte Fachkräfte.",
            style = "margin: 0; color: #6c757d;")
      ),
      
      div(style = "text-align: center; margin-top: 20px;",
          tags$em("Referenz: Spitzer, C. et al. (2021). Psychotherapie - Psychosomatik - Medizinische Psychologie, 71, 284-293.",
                  style = "color: #6c757d; font-size: 0.9em;")
      )
    )
  })
  
  output$your_position <- renderText({
    if (values$calculated) {
      paste0(values$total_score, " Punkte (T-Wert: ", values$t_value, ")")
    } else ""
  })
  
  # Normalverteilungsbalken generieren - KORRIGIERT für realen T-Wert Bereich (45-82)
  output$normal_distribution_bars <- renderUI({
    # T-Werte von 45 bis 80 (realer Bereich laut Tabelle), alle 2er Schritte
    # T=45 ist MINIMUM (12 Punkte), T=82 ist MAXIMUM (48 Punkte)
    t_values <- seq(45, 79, by = 2)
    
    # Vordefinierte normalisierte Höhen für T=45-79 (basiert auf dnorm, dann skaliert)
    # Peak bei T=50, abfallend zu den Seiten
    heights <- c(
      69, 80, 92, 105, 117, 129, 139, 148, 150, 148, 139, 129, 117, 105, 92, 80, 69, 55
    )
    
    # Erstelle Balken
    bars <- lapply(1:length(t_values), function(i) {
      t_val <- t_values[i]
      height <- heights[i]
      
      # Bestimme Farbe basierend auf T-Wert-Bereich
      if (t_val <= 45) {
        color <- "#28a745"  # Grün
        opacity <- "0.8"
      } else if (t_val <= 55) {
        color <- "#17a2b8"  # Blau  
        opacity <- "0.8"
      } else if (t_val <= 69) {
        color <- "#ffc107"  # Gelb
        opacity <- "0.8"
      } else {
        color <- "#dc3545"  # Rot
        opacity <- "0.8"
      }
      
      # Position basierend auf T-Wert (45-79 auf 50px-450px mappen)
      position <- 50 + ((t_val - 45) / 34) * 400
      
      div(style = paste0(
        "position: absolute; ",
        "left: ", position, "px; ",
        "bottom: 25px; ",
        "width: 15px; ",
        "height: ", height, "px; ",
        "background: ", color, "; ",
        "border-radius: 2px 2px 0 0; ",
        "opacity: ", opacity, "; ",
        "transition: all 0.3s ease;"
      ))
    })
    
    return(tagList(bars))
  })
  
  # Dynamische Positionierung des Benutzerwerts im Verteilungsdiagramm
  output$user_position_marker <- renderUI({
