# T-Werte zu Perzentil Konvertierung - KONTINUIERLICH & PRÄZISE
calculate_percentile <- function(score) {
  t_values <- c(45, 46, 48, 50, 51, 53, 54, 55, 56, 57, 58, 59, 61, 62, 63, 64, 65, 66, 67, 68, 69, 70, 72, 72, 74, 75, 76, 78, 78, 79, 80, 82, 82, 82, 82, 82, 82)
  names(t_values) <- 12:48
  
  if (score < 12) return(1)
  if (score > 48) return(99)
  
  t_val <- t_values[as.character(score)]
  if (is.na(t_val)) t_val <- 50
  
  # Kontinuierliche Normalverteilungs-CDF Approximation
  # T-Werte: T = 50 + 10*z, also z = (T-50)/10
  z_score <- (t_val - 50) / 10
  
  # Begrenzen auf sinnvolle Werte
  z_score <- pmax(-3, pmin(3, z_score))
  
  # Präzise Approximation der Standard-Normalverteilung CDF
  # Abramowitz & Stegun Approximation (sehr genau)
  if (z_score >= 0) {
    # Für positive z-scores
    t <- 1 / (1 + 0.2316419 * z_score)
    d <- 0.3989423 * exp(-z_score * z_score / 2)
    prob <- 1 - d * t * (0.3193815 + t * (-0.3565638 + t * (1.781478 + t * (-1.821256 + t * 1.330274))))
  } else {
    # Für negative z-scores: Φ(-z) = 1 - Φ(z)
    z_pos <- -z_score
    t <- 1 / (1 + 0.2316419 * z_pos)
    d <- 0.3989423 * exp(-z_pos * z_pos / 2)
    prob <- d * t * (0.3193815 + t * (-0.3565638 + t * (1.781478 + t * (-1.821256 + t * 1.330274))))
  }
  
  # Konvertiere zu Perzentil (0-100)
  percentile <- round(prob * 100)
  
  # Sicherstellen dass Werte im sinnvollen Bereich sind
  return(pmax(1, pmin(99, percentile)))
}

